    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        String sql = "insert into cliente (cod_cli,nome_cli,endereco,cidade,cep,uf) "
                + "values (" + jTextField1.getText()
                + ",'" + jTextField2.getText()
                + "','" + jTextField3.getText()
                + "','" + jTextField4.getText()
                + "','" + jFormattedTextField1.getText()
                + "','SP');";
        try {
            st.executeUpdate(sql);
            JOptionPane.showMessageDialog(null, "Registro Gravado");
        } catch (Exception e) {
            JOptionPane.showMessageDialog(null, "Registro n√£o Gravado");
        }

    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        try {
            con = DriverManager.getConnection("jdbc:mysql://localhost:3307/senacbdroger", "root", "");
            st = (Statement) con.createStatement();
            JOptionPane.showMessageDialog(null, "Conectado");
        } catch (Exception e) {
            JOptionPane.showMessageDialog(null, "N√£o Conectado");
        }
    }//GEN-LAST:event_jButton2ActionPerformed

